    &lt;script&gt;
        function backup() {
            var base_url = window.location.origin;
            swal({
                title: &quot;Backup to PC?&quot;,
                type: &quot;info&quot;,
                showCancelButton: true,
                closeOnConfirm: true,
            }, function () {
                window.location.replace(&quot;&lt;?php echo base_url() . 'parish/backup?parish_name=' . $parishdetails['name']; ?&gt;&quot;);
            });
        }
        function checkConnection() {
            swal({
                title: &quot;Connect to server?&quot;,
                type: &quot;info&quot;,
                showCancelButton: true,
                closeOnConfirm: false,
                showLoaderOnConfirm: true,
            }, function () {
//                var base_url = window.location.origin;
                var base_url = &quot;http://diocese-manager.esy.es&quot;;
                $.ajax({
                    type: &quot;GET&quot;,
                    dataType: 'json',
                    url: base_url + &quot;/sync/authenticate&quot;,
                    timeout: &quot;5000&quot;,
                    data: {pid: '&lt;?php echo $parishdetails['mparish_id']; ?&gt;'},
                    cache: false,
                    success: function (result) {
                        var base_url = window.location.origin;
                        if (result['status'] == true) {
                            swal({
                                title: &quot;Connected&quot;,
                                text: &quot;Server connection successfull, proceed to upload?&quot;,
                                type: &quot;success&quot;,
                                showCancelButton: true,
                                confirmButtonColor: &quot;#DD6B55&quot;,
                                confirmButtonText: &quot;Yes&quot;,
                                closeOnConfirm: false
                            }, function () {
                                window.location = base_url + &quot;/ParishManager/parish/sync?sync_btn=true&amp;p_id=&lt;?php echo $this-&gt;myencrypt-&gt;encrypt_url($parishdetails['mparish_id']); ?&gt;&quot;;
                            });
                        } else {
                            swal(&quot;Failed!&quot;, &quot;&quot; + result['error'], &quot;warning&quot;);
                        }
                    },
                    error: function (jqXHR, exception) {
                        var msg = '';
                        if (jqXHR.status === 0) {
                            msg = 'No Internet Connection.\n Verify Network.';
                        } else if (jqXHR.status == 404) {
                            msg = 'Requested page not found. [404]';
                        } else if (jqXHR.status == 500) {
                            msg = 'Internal Server Error [500].';
                        } else if (exception === 'parsererror') {
                            msg = 'Requested JSON parse failed.';
                        } else if (exception === 'timeout') {
                            msg = 'Time out error.';
                        } else if (exception === 'abort') {
                            msg = 'Ajax request aborted.';
                        } else {
                            msg = 'Uncaught Error.\n' + jqXHR.responseText;
                        }
                        swal(&quot;Failed!&quot;, msg, &quot;error&quot;);
                    }});
            });
        }
    &lt;/script&gt;
